// Importa useState y useEffect desde 'react'
import React, { useState, useEffect } from 'react';
// Importa io desde 'socket.io-client'
import { io } from 'socket.io-client';
// Importa los componentes UlMensajes y LiMensaje desde donde estén definidos
import { UlMensajes, LiMensaje } from './ui-components';

const socket = io('http://localhost:3000');

function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [nuevoMensaje, setNuevoMensaje] = useState('');
  const [usuario, setUsuario] = useState('');
  const [mensajes, setMensajes] = useState([]);

  useEffect(() => {
    socket.on('connect', () => {
      setIsConnected(true);
    });

    // Manejar la solicitud del nombre de usuario
    socket.on('solicitar_usuario', () => {
      const nombreUsuario = prompt('Ingresa tu nombre de usuario:');
      setUsuario(nombreUsuario);
      socket.emit('usuario', nombreUsuario);
    });

    socket.on('chat_message', (data) => {
      setMensajes((mensajes) => [...mensajes, data]);
    });

    return () => {
      socket.off('connect');
      socket.off('solicitar_usuario');
      socket.off('chat_message');
    };
  }, []);

  const enviarMensaje = () => {
    socket.emit('chat_message', {
      usuario: usuario,
      mensaje: nuevoMensaje,
    });
  };

  return (
    <div className="App">
      <h2>{isConnected ? 'CONECTADO' : 'NO CONECTADO'}</h2>
      <UlMensajes>
        {mensajes.map((mensaje, index) => (
          <LiMensaje key={index}>
            {mensaje.usuario}: {mensaje.mensaje}
          </LiMensaje>
        ))}
      </UlMensajes>
      <input type="text" onChange={(e) => setNuevoMensaje(e.target.value)} />
      <button onClick={enviarMensaje}>Enviar</button>
    </div>
  );
}

export default App;
